import {useState} from 'react';

export default function Search(props){

    const [cidade, setCidade] = useState("");

    function searchInput(e){
        e.preventDefault();
        // setCidade("");
        let currentValue = document.querySelector('input[name=searchInput]').value;

        // Request API
        const url = `https://api.openweathermap.org/data/2.5/weather?q=${currentValue}&appid=ec8686046d75b63040bab1bae08c984a&units=metric`;
        fetch(url)
        .then(response => response.json())
        .then(data => {
            const {main, name, sys, weather} = data;
            if(sys != undefined){

                if(weather != undefined){
                    const icon = `https://s3-us-west-2.amazonaws.com/s.cdpn.io/162656/${weather[0]["icon"]}.svg`;
                    setCidade(`
                        <div>
                            <p>${main.temp}</p>
                            <p>${sys.country} - ${name}</p>
                            <img src="${icon}"/>
                        </div>
                    `);
                }
            }else{
                setCidade("");
            }
        })

    }

    return(
        <div className="searchWraper">
            <div className="search">
                <h2>Busca por cidade üèôÔ∏è</h2>
                <form onSubmit={(e) => searchInput(e)}>
                    <input type="text" name="searchInput" 
                        placeholder={props.placeholder}/>
                    <input type="submit" value="Buscar" />
                </form>
            </div>
            <div className="searchResult">
                {
                    (cidade != "")?
                    <div dangerouslySetInnerHTML={{__html: cidade}} />:
                    <div>Pesquei por uma cidade</div>
                }
            </div>
        </div>
    )
}
